def largest_product_in_series(series_size, series)
   make_sub_sets(series_size, series)
        .filter{|sub_series| !sub_series.include?('0')}
        .map{|sub_series| find_product(sub_series)}
        .max
end

def find_product(sub_set)
    sub_set
        .split('')
        .map{|num| num.to_i}
        .reduce(1,:*)
end

def make_sub_sets(series_size, series)
    series_storage = [] 
    series
        .split('')
        .each_with_index{|num, index| 
            series_storage << series[index..(index+(series_size - 1))]
        }
    series_storage
end


series = ''

puts largest_product_in_series(4, series) == 5832 
puts largest_product_in_series(13, series) == 23514624000
